--
-- Структура таблицы `news`
--

CREATE TABLE `news` (
  `id` int(11) NOT NULL,
  `title` varchar(250) NOT NULL DEFAULT '',
  `content` text NOT NULL,
  `date` varchar(10) NOT NULL DEFAULT ''
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `news`
--

INSERT INTO `news` (`id`, `title`, `content`, `date`) VALUES
(2, 'Офіційний сайт', 'Наш офіційний сайт сьогодні запрацював!\r\nНа ньому ви зможете дізнатися про найостанніщі новини нашого центру, залишити свій відгук і прочитати інші відгуки про центр. Крім того на сайті будуть рохміщені корисні статті та документи для працівників та бітьків.\r\nКрім того, деякі функції ще розробляються нашими програмістами, тому очікуйте на оновлення.\r\nЛаскаво просимо!', '25-08-2018'),
(4, 'Конкурсний відбір на заняття посад', '«Марганецький інклюзивно-ресурсний центр» Марганецької міської ради оголошує з 18.03.2019 по 08.04.2019 включно прийом документів для участі у конкурсному відборі на заняття посад: вчителя - логопеда; вчителя - дефектолога; вчителя - реабілітолога; практичного психолога для комунальної установи «Марганецький інклюзивно-ресурсний центр» Марганецької міської ради, який знаходиться за адресою: м. Марганець, вул.. Перспективна, 3-А.<a href = \"https://mircenter2018.000webhostapp.com/article/show/4\">Дивитися докладніше</a>', '2019-03-28');

